import{_ as r,a as t,b as i,c as s,d as a,e as o,f as l}from"./chunks/Playback.BxzPZl2y.js";import{_ as n,o as u,c as d,ag as c}from"./chunks/framework.DEqXEGcv.js";const q=JSON.parse('{"title":"Interface : Éditeur","description":"","frontmatter":{},"headers":[],"relativePath":"fr/interface-editor.md","filePath":"fr/interface-editor.md"}'),p={name:"fr/interface-editor.md"};function g(m,e,h,f,_,v){return u(),d("div",null,[...e[0]||(e[0]=[c('<h1 id="interface-editeur" tabindex="-1">Interface : Éditeur <a class="header-anchor" href="#interface-editeur" aria-label="Permalink to &quot;Interface : Éditeur&quot;">​</a></h1><p>L&#39;Éditeur est le cœur du RhythmNator. C&#39;est ici que vous passerez le plus de temps à mapper vos fichiers audio.</p><p><img src="'+r+'" alt="Interface Complète"></p><hr><h2 id="_1-la-barre-superieure-top-bar" tabindex="-1">1. La Barre Supérieure (Top Bar) <a class="header-anchor" href="#_1-la-barre-superieure-top-bar" aria-label="Permalink to &quot;1. La Barre Supérieure (Top Bar)&quot;">​</a></h2><p>La barre supérieure regroupe les informations de projet et les actions globales.</p><p><img src="'+t+'" alt="Barre Supérieure"></p><ul><li><strong>À gauche</strong> : Affiche le titre de l&#39;outil (<strong>RhythmNator</strong>) et le nom de l&#39;outil de projet juste en dessous.</li><li><strong>À droite</strong> : Une série d&#39;icônes pour les actions rapides : <ul><li><strong>Accueil</strong> : Retourner à l&#39;écran de sélection de projet.</li><li><strong>Dossier</strong> : Ouvrir le dossier contenant les fichiers du projet.</li><li><strong>Sauvegarder</strong> : Enregistrer vos modifications.</li><li><strong>Export JSON</strong> : Exporter la partition finale au format JSON.</li><li><strong>Undo / Redo</strong> : Annuler ou rétablir les dernières actions.</li><li><strong>Sidebar</strong> : Ouvrir ou fermer la barre latérale gauche.</li></ul></li></ul><hr><h2 id="_2-zone-de-travail-piste" tabindex="-1">2. Zone de Travail (Piste) <a class="header-anchor" href="#_2-zone-de-travail-piste" aria-label="Permalink to &quot;2. Zone de Travail (Piste)&quot;">​</a></h2><p>La zone de travail est divisée en deux sections principales :</p><p><img src="'+i+'" alt="Visualiseur et Pistes"><img src="'+s+'" alt="Liste des Pistes"></p><ol><li><strong>Le Visualiseur</strong> : Permet de voir la forme d&#39;onde de la musique pour une précision visuelle lors du placement des notes.</li><li><strong>La Liste des Pistes</strong> : Affiche les différentes pistes créées avec les notes placées à l&#39;intérieur.</li><li><strong>Ligne de Lecture</strong> : Une ligne verticale qui indique précisément où vous vous trouvez dans la musique.</li></ol><blockquote><p>Pour apprendre à créer et gérer vos pistes, consultez le <a href="./guide-sheet.html">Guide : Zone de Travail (Piste)</a>.</p></blockquote><hr><h2 id="_3-panneau-audio" tabindex="-1">3. Panneau Audio <a class="header-anchor" href="#_3-panneau-audio" aria-label="Permalink to &quot;3. Panneau Audio&quot;">​</a></h2><p>Ce panneau permet de gérer tout ce qui concerne le fichier sonore et le rythme de base.</p><p><img src="'+a+'" alt="Panneau Audio"></p><ul><li><strong>Chargement</strong> : Zone pour charger ou modifier le fichier audio.</li><li><strong>BPM</strong> : Affiche le tempo actuel. Cliquez sur l&#39;icône <strong>Éclair</strong> pour détecter automatiquement le BPM.</li><li><strong>Décalage de départ</strong> : Ajustez la synchronisation initiale de la musique.</li><li><strong>Volume</strong> : Contrôlez le volume de lecture dans l&#39;éditeur.</li><li><strong>Vitesse de lecture</strong> : Modifiez la vitesse (ex: 0.5x, 1x, 2x) pour mapper les passages difficiles.</li><li><strong>Indicateur de Souris</strong> : Affiche ou masque la position de votre curseur sur la grille.</li></ul><hr><h2 id="_4-panneau-des-pistes" tabindex="-1">4. Panneau des Pistes <a class="header-anchor" href="#_4-panneau-des-pistes" aria-label="Permalink to &quot;4. Panneau des Pistes&quot;">​</a></h2><p>Gérez la structure de votre partition ici.</p><p><img src="'+o+'" alt="Panneau des Pistes"></p><ul><li><strong>Nouvelle Piste</strong> : Créer une piste additionnelle.</li><li><strong>Édition</strong> : Permet de créer ou de supprimer des notes manuellement.</li><li><strong>Sélection</strong> : Permet de sélectionner plusieurs pistes ou notes pour les déplacer, supprimer, copier ou coller.</li><li><strong>Gestionnaire de Groupe</strong> : Organisez vos pistes par groupes (ex: Batterie, Guitare).</li><li><strong>Marqueurs d&#39;Action</strong> : Gérez les événements spécifiques à certains moments de la musique.</li></ul><hr><h2 id="_5-le-lecteur-playback" tabindex="-1">5. Le Lecteur (Playback) <a class="header-anchor" href="#_5-le-lecteur-playback" aria-label="Permalink to &quot;5. Le Lecteur (Playback)&quot;">​</a></h2><p>Le lecteur vous offre un contrôle total sur l&#39;écoute de votre création.</p><p><img src="'+l+'" alt="Commandes de lecture"></p><ul><li><strong>Lecture / Pause</strong> : Lancez ou arrêtez l&#39;audio.</li><li><strong>Stop</strong> : Arrêtez la lecture et revenez au début.</li><li><strong>Navigation</strong> : Utilisez les boutons pour reculer ou avancer rapidement dans la musique.</li></ul>',29)])])}const z=n(p,[["render",g]]);export{q as __pageData,z as default};
