import{_ as t,a,b as o}from"./chunks/navigation.jTxDcg0y.js";import{_ as r,o as i,c as l,ag as n}from"./chunks/framework.DEqXEGcv.js";const y=JSON.parse('{"title":"Guide: Work Area (Track)","description":"","frontmatter":{},"headers":[],"relativePath":"guide-sheet.md","filePath":"guide-sheet.md"}'),s={name:"guide-sheet.md"};function h(c,e,d,u,g,p){return i(),l("div",null,[...e[0]||(e[0]=[n('<h1 id="guide-work-area-track" tabindex="-1">Guide: Work Area (Track) <a class="header-anchor" href="#guide-work-area-track" aria-label="Permalink to &quot;Guide: Work Area (Track)&quot;">​</a></h1><p>Learn how to manage tracks and organize your sheet in the &quot;Sheet&quot;.</p><h2 id="creating-a-track" tabindex="-1">Creating a Track <a class="header-anchor" href="#creating-a-track" aria-label="Permalink to &quot;Creating a Track&quot;">​</a></h2><ol><li>Go to the <strong>Track Management Panel</strong>.</li><li>Click the <strong>Create a New Track</strong> button.</li><li>A dialog will appear: <ul><li>Enter the <strong>Name</strong> of the new track.</li><li>Choose the <strong>Key</strong> (keyboard) that will be used to place notes.</li><li>Choose the <strong>Color</strong> of the notes for this track.</li></ul></li></ol><p><video src="'+t+'" controls="controls" muted="muted" style="max-height:640px;min-height:200px;"></video></p><h2 id="track-options" tabindex="-1">Track Options <a class="header-anchor" href="#track-options" aria-label="Permalink to &quot;Track Options&quot;">​</a></h2><p>At the top left of each track is a <strong>Label</strong>. Click it to open the options menu:</p><ul><li><strong>Edit</strong>: Allows you to change settings (name, key, color).</li><li><strong>Hide Track</strong>: Hides the track to declutter the view.</li><li><strong>Delete Track</strong>: Deletes the track and its notes.</li></ul><p><em>(Group assignment and note manipulation are covered in their respective guides: <a href="./guide-groups.html">Guide: Groups</a> and <a href="./guide-event-keys.html">Guide: Note</a>)</em></p><p><video src="'+a+'" controls="controls" muted="muted" style="max-height:640px;min-height:200px;"></video></p><h2 id="navigation" tabindex="-1">Navigation <a class="header-anchor" href="#navigation" aria-label="Permalink to &quot;Navigation&quot;">​</a></h2><p>Master movements in your project to edit with precision.</p><h3 id="the-player-playback" tabindex="-1">The Player (Playback) <a class="header-anchor" href="#the-player-playback" aria-label="Permalink to &quot;The Player (Playback)&quot;">​</a></h3><p>Located at the bottom of the screen, the player displays the total duration and current position.</p><ul><li><strong>Play / Pause</strong>: Starts or stops the music.</li><li><strong>Stop</strong>: Stops and returns to the beginning or the last starting point.</li><li><strong>Skip +/- 10s</strong>: Quickly advances or retreats by 10 seconds.</li><li><strong>Playback Bar</strong>: Drag the slider to reach a specific moment.</li></ul><h3 id="visualizer-and-work-area" tabindex="-1">Visualizer and Work Area <a class="header-anchor" href="#visualizer-and-work-area" aria-label="Permalink to &quot;Visualizer and Work Area&quot;">​</a></h3><ul><li><strong>Click on Visualizer</strong>: Click anywhere on the waveform (top) to instantly place the playhead there.</li><li><strong>Playhead Line</strong>: The vertical line indicates your exact position in the music.</li><li><strong>Scrollbars</strong>: Use horizontal and vertical bars to visually navigate the tracks area.</li></ul><h3 id="auto-follow" tabindex="-1">Auto-Follow <a class="header-anchor" href="#auto-follow" aria-label="Permalink to &quot;Auto-Follow&quot;">​</a></h3><p>The button on the far left of the player enables <strong>Auto-Follow</strong> mode.</p><ul><li><strong>Enabled</strong>: The view automatically scrolls to keep the playhead line visible.</li><li><strong>Disabled</strong>: The playhead line can go off-screen, allowing you to work on another section during playback.</li></ul><p><video src="'+o+'" controls="controls" muted="muted" style="max-height:640px;min-height:200px;"></video></p>',21)])])}const b=r(s,[["render",h]]);export{y as __pageData,b as default};
